window.addEventListener("load",function(){document.querySelectorAll("[ripple]").forEach(e=>{e.addEventListener("pointerdown",e=>{ripplet(e,{clearing:!1})}),e.addEventListener("pointerup",()=>{ripplet.clear(e)}),e.addEventListener("pointerleave",()=>{ripplet.clear(e)})})});
